# coding=utf-8import tracebackimport matplotlib.pyplot as pltimport numpy as npfrom meta_info import *result_root_this_script = join(results_root, 'statistic')class Dataframe_func:    def __init__(self,df,is_clean_df=True):        print('add lon lat')        df = self.add_lon_lat(df)        print('add landcover')        df = self.add_GLC_landcover_data_to_df(df)        print('add NDVI mask')        df = self.add_NDVI_mask(df)        print('add Aridity Index')        df = self.add_AI_to_df(df)        print('add ELI')        df = self.add_ELI_to_df(df)        print('add ELI_reclass')        df = self.ELI_reclass(df)        print('add AI_reclass')        df = self.AI_reclass(df)        # print('add ELI_significance')        # df = self.add_ELI_significance(df)        print('add koppen')        df = self.add_koppen(df)        if is_clean_df == True:            df = self.clean_df(df)        self.df = df    def clean_df(self,df):        df = df[df['lat']>=30]        # df = df[df['landcover_GLC'] != 'Crop']        df = df[df['NDVI_MASK'] == 1]        # df = df[df['ELI_significance'] == 1]        return df    def add_GLC_landcover_data_to_df(self, df):        f = join(data_root,'GLC2000/reclass_lc_dic.npy')        val_dic=T.load_npy(f)        val_list = []        for i, row in tqdm(df.iterrows(), total=len(df)):            pix = row['pix']            if not pix in val_dic:                val_list.append(np.nan)                continue            vals = val_dic[pix]            val_list.append(vals)        df['landcover_GLC'] = val_list        return df    def add_NDVI_mask(self,df):        # f =rf'C:/Users/pcadmin/Desktop/Data/Base_data/NDVI_mask.tif'        f = join(data_root, 'NDVI4g/NDVI_mask.tif')        print(f)        array, originX, originY, pixelWidth, pixelHeight = ToRaster().raster2array(f)        array = np.array(array, dtype=float)        val_dic = DIC_and_TIF().spatial_arr_to_dic(array)        f_name = 'NDVI_MASK'        print(f_name)        # exit()        val_list = []        for i, row in tqdm(df.iterrows(), total=len(df)):            pix = row['pix']            if not pix in val_dic:                val_list.append(np.nan)                continue            vals = val_dic[pix]            if vals < -99:                val_list.append(np.nan)                continue            val_list.append(vals)        df[f_name] = val_list        return df    def add_AI_to_df(self, df):        f = join(data_root, 'Aridity_Index/aridity_index.tif')        spatial_dict = DIC_and_TIF().spatial_tif_to_dic(f)        df = T.add_spatial_dic_to_df(df, spatial_dict, 'aridity_index')        return df    def add_lon_lat(self,df):        df = T.add_lon_lat_to_df(df, DIC_and_TIF())        return df    def add_ELI_to_df(self,df):        import analysis        f = join(analysis.Water_energy_limited_area().this_class_tif, 'ELI/GLEAM-ET_ERA-SM_Temperature.tif')        spatial_dict = DIC_and_TIF().spatial_tif_to_dic(f)        df = T.add_spatial_dic_to_df(df, spatial_dict, 'ELI')        return df    def ELI_reclass(self,df):        ELI_class = []        for i, row in tqdm(df.iterrows(), total=len(df)):            ELI = row['ELI']            if ELI < 0:                ELI_class.append('Energy-Limited')            elif ELI > 0:                ELI_class.append('Water-Limited')            else:                ELI_class.append(np.nan)        df['ELI_class'] = ELI_class        return df    def AI_reclass(self,df):        AI_class = []        for i,row in df.iterrows():            AI = row['aridity_index']            if AI < 0.65:                AI_class.append('Arid')            elif AI >= 0.65:                AI_class.append('Humid')            elif np.isnan(AI):                AI_class.append(np.nan)            else:                print(AI)                raise ValueError('AI error')        df['AI_class'] = AI_class        return df    def add_koppen(self,df):        f = join(data_root, 'koppen/koppen_reclass_dic.npy')        val_dic = T.load_npy(f)        df = T.add_spatial_dic_to_df(df, val_dic, 'Koppen')        return df    # def add_ELI_significance(self,df):    #     from Chapter5 import analysis    #     f = join(Water_energy_limited_area().this_class_tif, 'significant_pix_r/ELI_Temp_significance.tif')    #     spatial_dict = DIC_and_TIF().spatial_tif_to_dic(f)    #     df = T.add_spatial_dic_to_df(df, spatial_dict, 'ELI_significance')    #    #     return dfclass Dataframe:    def __init__(self):        self.this_class_arr, self.this_class_tif, self.this_class_png = \            T.mk_class_dir('Dataframe', result_root_this_script, mode=2)        self.dff = join(self.this_class_arr, 'dataframe.df')        pass    def run(self):        df = self.copy_df()        df = self.__gen_df_init()        df = Dataframe_func(df).df        T.save_df(df, self.dff)        T.df_to_excel(df, self.dff)        pass    def __gen_df_init(self):        if not os.path.isfile(self.dff):            df = pd.DataFrame()            T.save_df(df,self.dff)            return df        else:            df,dff = self.__load_df()            return df    def __load_df(self):        dff = self.dff        df = T.load_df(dff)        T.print_head_n(df)        print('len(df):',len(df))        return df,dff    def copy_df(self):        import analysis        dff = analysis.Resistance_Resilience().dff        df = T.load_df(dff)        T.save_df(df, self.dff)        T.df_to_excel(df, self.dff)        return dfclass Hot_Normal_Rs_Rt:    '''    Rs/Rt in water limited and energy limited area under Normal/hot droughts    '''    def __init__(self):        self.this_class_arr, self.this_class_tif, self.this_class_png = \            T.mk_class_dir('Hot_Normal_Rs_Rt', result_root_this_script, mode=2)        self.drought_type_list = ['normal-drought', 'hot-drought']        p